mapper.select:
/*K8SMapper.selectK8SCount*/ SELECT c.provider_cd as providerCd ,IF(k.cnt IS NULL, 0, k.cnt) AS cnt FROM ( SELECT common_cd AS provider_cd FROM common_code pc WHERE common_group_cd = 'PROVIDER' ) c LEFT OUTER JOIN ( SELECT provider_cd, count(*) AS cnt FROM k8s cks GROUP BY provider_cd ) k ON c.provider_cd = k.provider_cd
AND c.provider_cd = #{providerCd}
ORDER BY c.provider_cd

mapper.insert:
INSERT INTO k8s ( 
	  k8s_name
    , provider_cd			 
    , k8s_desc
    , content
    , argocd_url
    , argocd_username
    , argocd_password
    , argocd_token
    , reg_id
    , reg_name
    , reg_date
    , mod_id
    , mod_name
    , mod_date)
VALUES(
	  #{k8sName}
	, #{providerCd}	
	, #{k8sDesc}
	, #{content}
	, #{argocdUrl}
	, #{argocdUsername}
	, #{argocdPassword}
	, #{argocdToken}
	, #{regId}
	, #{regName}
	, now()
	, #{regId}
	, #{regName}
	, now()
)

mapper.update:
UPDATE k8s
SET
	k8s_name = #{k8sName}
	, k8s_desc = #{k8sDesc}
	, content = #{content}
	, argocd_url = #{argocdUrl}
	, argocd_username = #{argocdUsername}
	, argocd_password = #{argocdPassword}
	, argocd_token = #{argocdToken}
	, mod_id = #{modId}
	, mod_name = #{modName}
	, mod_date = now()
WHERE k8s_id = #{k8sId}

mapper.select:
        SELECT count(*) as cnt
		FROM k8s
		WHERE k8s_name = #{k8sName}
