mapper.select:
/* OssMapper.selectOssList */



		AND (oss_cd = #{ossCd}
		 OR oss_cd IN (SELECT common_cd FROM common_code WHERE common_group_cd = 'OSS' AND code_desc = #{ossCd}))

		/* OssMapper.isOssInfoDuplicated */
        SELECT (CASE WHEN COUNT(*) > 0 THEN true ELSE false END) AS duplicated
          FROM oss
         WHERE oss_name   = #{ossName}
           AND SUBSTRING_INDEX(SUBSTRING_INDEX(oss_url, '/', 3), '//', -1) = SUBSTRING_INDEX(SUBSTRING_INDEX(#{ossUrl}, '/', 3), '//', -1)
           AND oss_username = #{ossUsername}

mapper.insert:
/* OssMapper.insertOss */
INSERT INTO oss (
      oss_cd
    , oss_name
    , oss_desc
    , oss_url
    , oss_username
    , oss_password
    , oss_token
    , reg_id
    , reg_name
    , reg_date
) VALUES (
      #{ossCd}
	, #{ossName}
	, #{ossDesc}
	, #{ossUrl}
	, #{ossUsername}
	, #{ossPassword}
	, #{ossToken}
	, #{regId}
	, #{regName}
	, now()
)

mapper.update:
/* OssMapper.updateOss */
UPDATE oss
SET
      oss_name = #{ossName}
    , oss_desc = #{ossDesc}
    , oss_url = #{ossUrl}
    , oss_username = #{ossUsername}
    , oss_password = #{ossPassword}
    , oss_token = #{ossToken}
    , mod_id = #{modId}
    , mod_name = #{modName}
    , mod_date = now()
WHERE
    oss_id = #{ossId}
